+        wallet.add(&record.type_.unwrap(), &record.name, &record.value.unwrap(), &record.tags.unwrap())?;
+        index = end_index;
+    }
+
+    let remaining = buff.len() - index;
+    for i in 0 .. remaining {
+        buff[i] = buff[index + i];
+    }
+    buff.resize(remaining, 0);
+
+    Ok(())
