+    T: MessageSender,
+{
+    info!("eth_getBlockByNumber");
+
+    let (block_num, full): (String, bool) = match params.parse() {
+        Ok(t) => t,
+        Err(_) => {
+            return Err(Error::invalid_params(
+                "Takes [blockNum: QUANTITY|TAG, full: BOOL]",
